
public class sumofarray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arr[] = new int[]{10,15,24,65,88};
		
		int sum=0;
		for(int i =0;i<arr.length;i++)
		{
			sum = sum+arr[i];
			
		}
		System.out.println("sum of all elements =" +sum);
		
	}

}

